const request = require('supertest');
const axios = require('axios');
const MockAdapter = require('axios-mock-adapter');
const app = require('../app');
const models = require('../models');

beforeAll(async () => {
  const mock = new MockAdapter(axios);
  mock.onPost().reply(200, {});
  await models.sequelize.sync({
    force: true,
  });
  await models.Sign.bulkCreate([
    {
      name: 'Test 1',
      url: 'http://www.testwebsite.com/message',
    },
    {
      name: 'Test 2',
      url: 'http://www.testwebsite2.com/message',
    },
  ]);
});

describe('Test message paths', () => {
  describe('Test manual message paths', () => {
    test('It should raise an error for an invalid binary string', () =>
      request(app)
        .post('/message/manual')
        .type('form')
        .send({ message: '01', signOptions: ['1', '2'] })
        .then(response => {
          expect(response.statusCode).toBe(200);
          expect(response.text).toContain(
            'Please enter a valid binary string. See above for details.',
          );
        }));
    test('It should raise an error for a missing road sign', () =>
      request(app)
        .post('/message/manual')
        .type('form')
        .send({
          message:
            '',
        })
        .then(response => {
          expect(response.statusCode).toBe(200);
          expect(response.text).toContain(
            'Please select at least one road sign to update.',
          );
        }));
    test('It should successfully respond to a correct POST request', () =>
      request(app)
        .post('/message/manual')
        .type('form')
        .send({
          message:
            '',
          signOptions: ['1', '2'],
        })
        .then(response => {
          expect(response.statusCode).toBe(200);
          expect(response.text).toContain('Success!');
        }));
  });
  describe('Test text message paths', () => {
    test('It should raise an error for an error for missing first line', () =>
      request(app)
        .post('/message/text')
        .type('form')
        .send({ line1: '', signOptions: ['1', '2'] })
        .then(response => {
          expect(response.statusCode).toBe(200);
          expect(response.text).toContain(
            'Please enter a valid message for line 1. See above for details.',
          );
        }));
    test('It should raise an error for too long line', () =>
      request(app)
        .post('/message/text')
        .type('form')
        .send({
          line1: 'This line is too long for line one',
          signOptions: ['1', '2'],
        })
        .then(response => {
          expect(response.statusCode).toBe(200);
          expect(response.text).toContain(
            'Please enter a valid message for line 1. See above for details.',
          );
        }));
    test('It should raise an error for a missing road sign', () =>
      request(app)
        .post('/message/text')
        .type('form')
        .send({
          line1: 'Hello',
          line2: 'World',
        })
        .then(response => {
          expect(response.statusCode).toBe(200);
          expect(response.text).toContain(
            'Please select at least one road sign to update.',
          );
        }));
    test('It should successfully respond to a correct POST request', () =>
      request(app)
        .post('/message/text')
        .type('form')
        .send({
          line1: 'Hello',
          line2: 'World',
          signOptions: ['1', '2'],
        })
        .then(response => {
          expect(response.statusCode).toBe(200);
          expect(response.text).toContain('Success!');
        }));
  });

  describe('Test image message paths', () => {
    test('It should raise an error for an invalid image URL', () =>
      request(app)
        .post('/message/image')
        .type('form')
        .send({ message: 'notanimagefile', signOptions: ['1', '2'] })
        .then(response => {
          expect(response.statusCode).toBe(200);
          expect(response.text).toContain(
            'Please enter a valid image URL, of type .png or .jpeg',
          );
        }));
    test('It should raise an error for a missing road sign', () =>
      request(app)
        .post('/message/image')
        .type('form')
        .send({ message: 'notanimagefile' })
        .then(response => {
          expect(response.statusCode).toBe(200);
          expect(response.text).toContain(
            'Please select at least one road sign to update.',
          );
        }));
    test('It should successfully respond to a correct POST request', () =>
      request(app)
        .post('/message/image')
        .type('form')
        .send({
          message:
            'https://en.facebookbrand.com/wp-content/uploads/2016/05/FB-fLogo-Blue-broadcast-2.png',
          signOptions: ['1', '2'],
        })
        .then(response => {
          expect(response.statusCode).toBe(200);
          expect(response.text).toContain('Success!');
        }));
  });
});
